import { Meteor } from 'meteor/meteor'
import { dockerStatus } from '../imports/collections.js'
import { DockerEvents } from 'docker-events'

const Future = require('fibers/future')

// Docker API - npm modules
var Docker = require('dockerode')
var emitter = new DockerEvents({docker: new Dockerode()})

var docker = new Docker({host: 'http://meteor-vm.ldd.rds.lexmark.com', port: '2375' })

Meteor.startup(() => {

//  WebApp.rawConnectHandlers.use(function(req, res, next){
//  	res.setHeader('Access-Control-Allow-Origin','*')
//  	next()
//  })

});

Meteor.methods({

	_dockerList: function(){
		docker.listContainers(Meteor.bindEnvironment(function(err,result){
			var rec = { item: 'dockerList', data: result }
  			dockerStatus.upsert({ item: 'dockerList'}, rec)
  		}))  		
	}

})

var cont = docker.getContainer('84f389d5484161358c18f7e3319cfe487a224e595b6c5e5b671838bb86e71b0a')

emitter.start()

emitter.on('connect', function(){
	console.log('Docker API Connected')
})

emitter.on('_message', function(message){
	console.log(`...from docker: ${message}`)
})
